function addEvent(el,type,fn){
	if(el.addEventListener){
		el.addEventListener(type,fn,false)
	}else if(el.attachEvent()){
		el.attachEvent('on'+type,fn,false)
	}else{
		return false
	}
}

var formateString = function(str,obj){

	return str.replace(/\{#(\w+)#\}/g,function(match,key){
		return obj[key]
	})
}
var createBtn = function(id,src,type){
	type = type || 'base64'
	var html = '<div class="btn" id="{#id#}"><img src="{#src#}"></div>';
	if(type=="base64"){
		src = "data:image/png;base64,"+ src
	}
	var obj = {}
	obj["id"] = id
	obj["src"] = src
	return formateString(html,obj)
}
var createInput = function(id,src){
	var tpl = ['<div class="inputButton" style="width:100px;">',
	'<input id="{#id#}"  type="text" style="padding: 0px 30px 0px 0px; text-align: center; border-width: 0px; background: rgb(174, 174, 177); width: 70px; height: 30px; border-radius: 0px; position: absolute; color: rgb(51, 51, 51); left: 0px; top: 0px;">',
	'<div class="goBtn"><img src="data:image/png;base64,{#src#}"></div>',
	'</div>']
	tpl = tpl.join("");
	var obj = {};
	obj['id'] = id
	obj['src'] = src;
	//obj['content'] = content;
	return formateString(tpl,obj)


}
var createTap = function(){
	var rootdom = $('<div></div>');
	rootdom.attr('id','bottom_tap');
	rootdom.css({width:"100%",background:"#000",height:"50px","z-index": "999",position: "absolute",bottom:"0"})
	var center = $('<div></div>')
	center.css({width:"336px",height:"100%",margin:"0 auto"})

	var contentBox = $('<div id="contentBox" class="phoneGotoPageContent"></div>')
	contentBox.css({width: "100%", height: "50px", "text-align": "center", display: "inline-block"})
	var btnHtml  = ''
	btnHtml+= createBtn("to_first",toolbar_icons.first,"base64");
	btnHtml+= createBtn("to_pre",toolbar_icons.prev,"base64")
	btnHtml+= createInput("currentPageIndexTextField",toolbar_icons.gobtn)
	btnHtml+= createBtn("to_next",toolbar_icons.next,"base64")
	btnHtml+= createBtn("to_last",toolbar_icons.Last,"base64")
	contentBox.html(btnHtml)
	center.append(contentBox)
	rootdom.append(center)
	return rootdom
}
var createTop = function(){
	var rootdom = $('<div></div>');
	rootdom.attr('id','top_tap');
	rootdom.css({width:"100%",background:"#000",height:"50px",position: "absolute",top:"0","z-index":"999"});
	var img = $('<img>')
	img.attr('src','./images/appLogoIcon.png');
	img.css({"position":"absolute",'height':'40px','cursor':'pointer','top':'3px'});
	rootdom.append(img)
	return rootdom;
}

function isChrome() {

	return navigator.userAgent.indexOf('Chrome')!=-1;

}

function displayMode(){
	
	if (window.orientation == 180 || window.orientation == 0) {
         //alert("竖屏状态！")
         return 'single'
     }
      if (window.orientation == 90 || window.orientation == -90) {
         // alert("横屏状态！")
         return 'double'
     }
     return 'double';
}

$(document.body).append(createTop());
$(document.body).append(createTap());
var btnEvents = {
	to_first:function(e){
		$('.magazine').turn('page',1);
	},
	to_prev:function(){
		var magazine = $(".magazine");
		if(magazine.turn("page") - 1 < 1){
			return;
		}else{
			magazine.turn("previous");
		}
		
	},
	to_next:function(){
		var magazine = $(".magazine");
		if(magazine.turn("page") + 1 > magazine.turn('pages')){
			return;
		}
		magazine.turn("next")
	},
	to_last:function(){
		$('.magazine').turn("page",$('.magazine').turn('pages'))
	},
	go_page : function(){
		var magazine = $('.magazine');
		var page = parseInt($("#currentPageIndexTextField").val()) || magazine.turn('page');
		var inputstr = "/"+magazine.turn("pages");
		inputEle = $("#currentPageIndexTextField");
		var inputstr = "/"+magazine.turn("pages");
		if(page > magazine.turn("pages")){//大于总页数
			page = magazine.turn("pages")
		}else if(page < 1){//少于总页数
			page = 1;
		}
		magazine.turn("page",page);
		inputEle.val(magazine.turn("page")+inputstr);

		resizeViewport(); 
	}
}
$("#to_first").click(btnEvents.to_first)
$("#to_pre").click(btnEvents.to_prev)
$("#currentPageIndexTextField").focus(function(){$("#currentPageIndexTextField").val("");resizeViewport(); })

$("#to_next").click(btnEvents.to_next)
$("#to_last").click(btnEvents.to_last)
$(".goBtn").click(btnEvents.go_page)

function loadApp(){
	$("#canvas").fadeIn(1000);
	var cvsHeight = $("#bottom_tap").offset().top - 70
	var cvsWidth = $.isTouch?cvsHeight * 0.73:cvsHeight * 0.73 *2
	var flipbook = $('.magazine');
	if(flipbook.width() == 0 || flipbook.height()==0){
		setTimeout(loadApp,10);//异步刷新
		return;
	}

	//create flipbook
	flipbook.turn({
		width:cvsWidth,
		height:cvsHeight,

		acceleration:!isChrome(),

		gradients: true,

		autoCenter:true,

		elevation:50,
		page:1,
		display:displayMode(),
		pages:8,

		//events
		when:{
			turning:function(event,page,view){
				var book = $(this),
				currentPage = book.turn('page'),
				pages = book.turn('pages');
				$("#currentPageIndexTextField").val(page+"/"+pages)
				//Update then current URI
				Hash.go('page/'+page).update();
				//添加翻页音乐
				//document.getElementById('flipSound').play()
			},
			turned:function(event,page,view){
				$(this).turn('center');
			},
			missing:function(event,pages){
				//add pages that arent in the magazine
				for(var i = 0;i<pages.length;i++){
					addPage(pages[i],$(this));
				}
			}
		},

		
	});
	$("#currentPageIndexTextField").val("1/"+$(".magazine").turn("pages"))
	$('.magazine-viewport').zoom({
		flipbook: $('.magazine'),
		max: function() { 
			
			return largeMagazineWidth()/$('.magazine').width();
			//return 2;
		}, 
	})


	if ($.isTouch)
		$('.magazine-viewport').bind('zoom.doubleTap', zoomTo);
	else
		$('.magazine-viewport').bind('zoom.tap', zoomTo);

	Hash.on('^page\/([0-9]*)$', {
		yep: function(path, parts) {
			var page = parts[1];

			if (page!==undefined) {
				if ($('.magazine').turn('is'))
					$('.magazine').turn('page', page);
			}

		},
		nop: function(path) {

			if ($('.magazine').turn('is'))
				$('.magazine').turn('page', 1);
		}
	});


	// Regions

	if ($.isTouch) {
		$('.magazine').bind('touchstart', regionClick);
	} else {
		$('.magazine').click(regionClick);
	}

	$(window).resize(function() {
		resizeViewport();
	}).bind('orientationchange', function() {
		resizeViewport();
	});

	resizeViewport();
	$('.magazine').addClass('animated');
}
 $('#canvas').hide();
yepnope({
	test : Modernizr.csstransforms,
	yep: ['./js/turn.js'],
	nope: ['./js/turn.html4.min.js'],
	both: ['./js/zoom2.js', './js/magazine.js', './css/magazine.css'],
	complete: loadApp
});

